[@(Html.EJ().Gantt("ganttsample"). .ToolbarSettings(tl =>
                 {
                     tl.ShowToolbar(true);
                     tl.ToolbarItems(new List<GanttToolBarItems>()
             {
                 GanttToolBarItems.Add,
                GanttToolBarItems.Edit,
                     });
               }))]

[@(Html.EJ().Gantt("ganttsample")
 .ColumnDialogFields(new List<GanttColumnDialogFields>() {
 GanttColumnDialogFields.Field, 
 GanttColumnDialogFields.HeaderText,
 GanttColumnDialogFields.EditType
 }))]

[@(Html.EJ().TreeGrid("TreeGridContainer")
                   .ToolbarSettings(tool =>{tool.ShowToolbar(true);
                   tool.ToolbarItems(new List<TreeGridToolBarItems>(){
                    TreeGridToolBarItems.Add, TreeGridToolBarItems.Edit,TreeGridToolBarItems.Delete,TreeGridToolBarItems.Update,TreeGridToolBarItems.Cancel,TreeGridToolBarItems.ExpandAll,TreeGridToolBarItems.CollapseAll,});}))]
